import React from "react";

const data = { question: "question", answer: "la réponse" };

export default function Accordion() {
  return (
    <div className="Container">
      <div>
        <span className="accordion-title">question</span>
        <h1>les rep</h1>
      </div>
      <div className="'accordion-faq">
        {/* {data.map((item,index) =>(
            <div key={index}>
                <div className="accordion-faq-heading">
                    <h3>{item.question}</h3>
                </div>
                <div>
                    <span className="verticle">-</span>
                </div>
                <div>
                    <p>{item.answer}</p>
                </div>
            </div>
        ))} */}
        {data.map( (i)=>{
            return(
                <div key={index}>
                <div className="accordion-faq-heading">
                    <h3>{item.question}</h3>
                </div>
                <div>
                    <span className="verticle">-</span>
                </div>
                <div>
                    <p>{item.answer}</p>
                </div>
            </div>
            )
        } )}
      </div>
    </div>
  );
}
