import React from "react";
import styled from "styled-components";
// import BannerImg from "../assets/images/Banner-img.png"; // with import

const StyledBanner = styled.div`
  border-radius: 25px;
  height: 250px;
  width: 80%;
  margin: auto;
  position: relative;
  text-align: center;

  img {
    height: 100%;
    width: 100%;
    border-radius: 25px;
    object-fit: c;
  }
  .filter {
    background-color: black;
    opacity: 0.3;
    height: 100%;
    width: 100%;
    border-radius: 25px;
    position: absolute;
    top: 0px;
    mix-blend-mode: darken;
  }
  p {
    font-family: "Montserrat";
    font-style: normal;
    font-weight: 500;
    font-size: 48px;
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    margin: auto;
    color: white;
    z-index: 2;
    width: 100%;
  }
`;

export default function Banner(props) {
  return (
    <div>
      <StyledBanner>
        <img src='https://s3-eu-west-1.amazonaws.com/course.oc-static.com/projects/front-end-kasa-project/accommodation-20-2.jpg' alt="nature img" />
        <p>Chez vous, partout et ailleurs</p>
        <div className="filter"></div>
      </StyledBanner>
    </div>
  );
}
